	<script>
		document.getElementById("year").innerHTML = new Date().getFullYear();
		$('figure.easyimage ').find('img').addClass('img-fluid')
		var no = '298 6031005'
		var cek62 = no.slice(0, 2)
		var cek0 = no.slice(0, 1)
		var phone = ''
		if (cek62 == 62) {
			phone = "(+62) " + no.slice(2, 15)
			$('#tlp').text(phone)
			$('#tlp1').text(phone)
		} else if (cek0 == 0) {
			phone = "(+62) " + no.slice(1, 15)
			$('#tlp').text(phone)
			$('#tlp1').text(phone)
		} else {
			phone = "(+62) " + no.slice(0, 15)
			$('#tlp').text(phone)
			$('#tlp1').text(phone)
		}
	</script>
	<script>
	$(document).ready(function () {
		function cls() {
			$('#p_message').val('')
			$('#p_name').val('')
			$('#p_email').val('')
			$('#p_subject').val('')
			grecaptcha.reset();
		}
		$('#contactForm').submit(function (e) {
			e.preventDefault()
			ga('send', {
				'hitType': 'event',          // Required.
				'eventCategory': 'button',   // Required.
				'eventAction': 'click',      // Required.
				'eventLabel': 'contact form'
			});
			console.log("ga : " + ga)
			swal({
				icon: '/img/loading.svg',
				text: 'Memuat Data . . .',
				buttons: false,
				closeOnClickOutside: false,
			});
			$.ajax({
				type: "POST",
				url: "/admin/contact/insert/act",
				data: {
					name: $('#p_name').val(),
					email: $('#p_email').val(),
					subject: $('#p_subject').val(),
					message: $('#p_message').val(),
					captcha: grecaptcha.getResponse()
				},
				dataType: "json",
				success: function (response) {
					console.log("Response : " + response.status)
					if (response.status == 200) {
						swal({
							title: 'success',
							icon: 'success',
							text: response.message,
							timer: 2500
						});
						cls()
					} else {
						swal({
							title: 'error',
							icon: 'error',
							text: response.message,
							timer: 2500
						});
						cls()
					}
				},
				error: function (response) {
					swal({
						title: 'error',
						icon: 'error',
						text: response.message,
						timer: 2500
					});
					cls()
				}
			});
		})
	});
</script>


